@default-banner-height: 162px;

& {
  color: @text-color;
  position: absolute;
  font-size: 40px;
  bottom: 0px;
  left: 0px;
  width: 100%;
  height: @default-banner-height;
  .default-transition();
}

& {
  background:fade(@primary-color, 0%);
  visibility: hidden;
}

&.component-visible {
  background: fade(@primary-color, 95%);
  visibility: visible;
}

.individual {
  .default-transition();
  position: absolute;
  width: 100%;
  height: @default-banner-height;
  top: 0;
  left: 0;
  text-align: center;
  opacity: 0;
  visibility: hidden;
  padding: 0;
  margin: 0;
}

.individual.component-visible {
  opacity: 1;
  visibility: visible;
}

.title {
  font-family: Arial;
  font-size: 77px;
  margin: -10px 0 0 0;
  padding: 0;
}

.subtitle {
  font-family: Arial;
  font-style: italic;
  font-size: 60px;
  margin: -30px 0 0 0;
  padding: 0;
}

p {
  margin-top: -30px;
}

